import React, { useState } from "react";
import { useQuery } from "react-query";

import host from "../utils/host";

type CommunityType = {
  uuid: string;
  name: string;
  description: string;
  createdAt: string;
  updatedAt: string;
};

const Communities: React.FC = () => {
  const [page, setPage] = useState<number>(1);

  const fetchCommunities = (page: number = 1) =>
    fetch(`${host}/communities?page=${page}&limit=5`).then((res) => res.json());

  const {
    isLoading,
    isError,
    error,
    data,
    isFetching,
    isPreviousData,
  } = useQuery<CommunityType[], Error>(
    ["communities", page],
    () => fetchCommunities(page),
    {
      keepPreviousData: true,
    }
  );

  // testing
  React.useEffect(() => {
    console.log(data);
  }, [data]);

  return (
    <React.Fragment>
      <div className="communities-container">
        {isLoading ? (
          <div>Loading...</div>
        ) : isError ? (
          <div>Error: {error.message}</div>
        ) : (
          <React.Fragment>
            {data.map((community) => (
              <div className="community-container" key={community.uuid}>
                <p>{community.name}</p>
                <p>{community.description}</p>
              </div>
            ))}
          </React.Fragment>
        )}
        <span>Current Page: {page}</span>
        <button>Previous Page</button>
      </div>
    </React.Fragment>
  );
};

export default Communities;
