import React from "react";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSortUp, faSortDown } from "@fortawesome/free-solid-svg-icons";

import { QuestionType } from "../utils/types/individualQuestionType";

interface Props {
  data: QuestionType;
}

const Question: React.FC<Props> = ({ data }) => {
  const style = { cursor: "pointer", display: "block" };

  return (
    <React.Fragment>
      <div className="question-container">
        <h1>{data.title}</h1>
        <div className="h1-border-btm" />
        <div className="main">
          <div className="votes">
            <FontAwesomeIcon icon={faSortUp} color={"white"} style={style} />
            <FontAwesomeIcon icon={faSortDown} color={"white"} style={style} />
          </div>
          <div className="main-right">
            <ReactMarkdown source={data.body} plugins={[remarkGfm]} />
          </div>
        </div>
      </div>

      <style jsx>{`
        .question-container {
          width: 80%;
          margin: 30px auto;
        }

        h1 {
          font-size: 1.3rem;
          line-height: 1.4em;
          margin: 10px auto;
          width: 98%;
        }

        .h1-border-btm {
          width: 100%;
          margin: 10px 0;
          border-bottom: 1px solid #fff;
        }

        .main {
          display: flex;
          margin: 20px 0;
        }

        .votes {
          width: 5%;
        }

        .main-right {
          width: 95%;
        }
      `}</style>
    </React.Fragment>
  );
};

export default Question;
