import React from "react";
import { useRouter } from "next/router";
import { useQuery } from "react-query";

import host from "../../../utils/host";

type CommunityType = {
  uuid: string;
  name: string;
  description: string;
  createdAt: string;
  updatedAt: string;
};

const PostPage: React.FC = () => {
  const router = useRouter();
  const { community, uuid } = router.query;

  const fetchCommunities = async () => {
    const res = await fetch(`${host}/communities/${community}`);
    const data: CommunityType = await res.json();
    return data;
  };

  const { isLoading, isError, error, data } = useQuery<CommunityType, Error>(
    ["check-community"],
    () => fetchCommunities(),
    { cacheTime: 0 }
  );

  return (
    <React.Fragment>
      <main>
        {isLoading ? (
          <div>Loading...</div>
        ) : isError ? (
          <div>{error.message}</div>
        ) : data.uuid ? (
          <div>{uuid}</div>
        ) : (
          <div>Community doesn't exist</div>
        )}
      </main>
    </React.Fragment>
  );
};

export default PostPage;
