import React from "react";
import shave from "shave";

import { tagBackground } from "../utils/cssVariables";

interface Props {
  uuid: string;
  name: string;
  description: string;
  count: number;
}

const Community: React.FC<Props> = ({ uuid, name, description, count }) => {
  const desc = document.getElementsByClassName("description");
  Array.from(desc).forEach((item) => {
    shave(item, 40);
  });

  return (
    <React.Fragment>
      <div className="community-container">
        <div className="name-count">
          <span className="background">
            <p className="name">{name}</p>
          </span>
          <p className="count">Number of Posts: {count}</p>
        </div>
        <p className="description">{description}</p>
      </div>

      <style jsx>{`
        .community-container {
          display: block;
          width: 95%;
          height: 120px;
          margin: 0 auto;
          border-top: 1px solid #ffff;
        }

        .name-count {
          display: flex;
          justify-content: space-between;
          align-items: center;
          height: 50%;
        }

        .background {
          background: ${tagBackground};
          border-radius: 4px;
        }

        .name {
          padding: 2px 7px;
        }

        .description {
          margin-bottom: 25px;
          height: calc(50% - 20px);
          border: 1px solid red;
          //overflow: hidden;
        }
      `}</style>
    </React.Fragment>
  );
};

export default Community;
