import React, { useState } from "react";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";

import * as css from "../utils/cssVariables";
import { CommentType } from "../utils/types/individualQuestionType";

interface Props {
  setEditMode: React.Dispatch<React.SetStateAction<boolean>>;
  comment: CommentType;
}

const EditAnswer: React.FC<Props> = ({ setEditMode, comment }) => {
  const [body, setBody] = useState<string>(comment.body);

  return (
    <React.Fragment>
      <form className="edit-answer-body">
        <textarea
          id="edit-form-body"
          cols={30}
          rows={10}
          value={body}
          onChange={(e) => setBody(e.target.value)}
        />
        <ReactMarkdown
          className="preview edit-post-preview"
          source={body}
          plugins={[remarkGfm]}
        />
      </form>
    </React.Fragment>
  );
};

export default EditAnswer;
