import React, { useState } from "react";
import ReactMde from "react-mde";
import Showdown from "showdown";

const converter = new Showdown.Converter({
  tables: true,
  simplifiedAutoLink: true,
  strikethrough: true,
  tasklists: true,
});

const RequestPostPage: React.FC = () => {
  const [value, setValue] = useState<string>("**Hello World!**");
  const [selectedTab, setSelectedTab] = useState<"preview" | "write">("write");

  return (
    <React.Fragment>
      <h1 className="post-form-heading">Ask a Question</h1>
      <form className="post-form-container">
        <input
          type="text"
          className="post-form-input"
          id="title"
          placeholder="Title"
          required
        />
        <ReactMde value={value} onChange={setValue} selectedTab={selectedTab} />
      </form>
    </React.Fragment>
  );
};

export default RequestPostPage;
