import React from "react";

import * as css from "../utils/cssVariables";
import { PostType } from "../utils/types/postType";
import Tag from "./Tag";

interface Props {
  post: PostType;
}

const CommunityPost: React.FC<Props> = ({ post }) => {
  return (
    <React.Fragment>
      <div className="community-post-container">
        <div className="community-post-aside">
          <div className="votes-container">
            <p className="number">{post.votes}</p>
            <p className="text">Vote{post.votes === 1 ? "" : "s"}</p>
          </div>
          <div className="comments-container">
            <p className="number">{post.comments.length}</p>
            <p className="text">
              Answer{post.comments.length === 1 ? "" : "s"}
            </p>
          </div>
        </div>
        <div className="community-post-main">
          <p className="post-title">{post.title}</p>
          <div className="main-bottom">
            {post.communities.map((community) => (
              <Tag name={community.name} key={community.name} />
            ))}
            <p className="asked-by">
              asked by: <span>{post.user.username}</span>
            </p>
          </div>
        </div>
      </div>

      <style jsx>{`
        .community-post-container {
          width: 90%;
          margin-top: 30px;
          background: ${css.floatingPostBackground};
          border-radius: 10px;
          height: 100px;
          display: flex;
        }

        .community-post-aside {
          width: 7%;
          display: flex;
          flex-direction: column;
          justify-content: center;
          margin: 0 5px;
        }

        .community-post-main {
          width: 93%;
          margin: 10px;
          display: flex;
          flex-direction: column;
          justify-content: space-between;
          align-items: flex-start;
        }

        .post-communities-container {
          display: flex;
        }

        .votes-container,
        .comments-container {
          width: 100%;
          height: 50%;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        .votes-container p,
        .comments-container p {
          color: #dedede;
          font-size: 1rem;
        }

        .votes-container .text,
        .comments-container .text {
          font-size: 0.7rem;
        }

        .post-title {
          color: #ffffff;
          cursor: pointer;
          font-size: 0.95rem;
          display: block;
          word-wrap: break-word;
          overflow: hidden;
          line-height: 1.2em;
          max-height: 2.4em;
          margin-left: 6px;
          margin-top: 3px;
        }

        .post-title:hover {
          color: #bebebe;
        }

        .main-bottom {
          display: flex;
          justify-content: flex-start;
          border: 1px solid red;
          width: 100%;
        }

        .asked-by {
          justify-self: flex-end;
        }
      `}</style>
    </React.Fragment>
  );
};

export default CommunityPost;
