import React, { ChangeEvent, useState, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";
import { useQuery } from "react-query";

import * as css from "../../utils/cssVariables";
import host from "../../utils/host";

type PostType = {
  uuid: string;
};

type CommunityType = {
  uuid: string;
  name: string;
  description: string;
  createdAt: string;
  updatedAt: string;
  posts: PostType[];
};

type DataType = {
  communities: CommunityType[];
  hasMore: boolean;
};

const RequestPostPage: React.FC = () => {
  const [body, setBody] = useState<string>("**Hello World!**");
  const [tags, setTags] = useState<string>("");

  const fetchCommunities = async (filter: string = "") => {
    const res = await fetch(
      `${host}/communities?page=1&limit=6&filter=${filter}`
    );
    return await res.json();
  };

  const { isLoading, error, data } = useQuery<DataType, Error>(
    ["communities", tags],
    () => fetchCommunities(tags)
  );

  useEffect(() => {
    const tagExpand = document.getElementById("expand");
    if (tags !== "") {
      tagExpand.style.display = "block";
    } else {
      tagExpand.style.display = "none";
    }
  }, [tags]);

  return (
    <React.Fragment>
      <h1 className="post-form-heading">Ask a Question</h1>
      <form className="post-form-container">
        <input
          type="text"
          className="post-form-input"
          id="title"
          placeholder="Title"
          required
        />
        <textarea
          id="body"
          className="post-form-body"
          cols={30}
          rows={10}
          placeholder="Body"
          required
          value={body}
          onChange={(e: ChangeEvent<HTMLTextAreaElement>) =>
            setBody(e.target.value)
          }
        />
        <ReactMarkdown
          className="preview"
          plugins={[remarkGfm]}
          source={body}
        />
        <div className="tags">
          <input
            type="text"
            className="post-form-input"
            placeholder="Tags: (maximum 5) "
            value={tags}
            onChange={(e: ChangeEvent<HTMLInputElement>) =>
              setTags(e.target.value)
            }
          />
          <div className="tags-expand" id="expand">
            {}
          </div>
        </div>
      </form>

      <style jsx>{`
        .post-form-heading {
          text-align: center;
          margin: 30px 0;
        }

        .post-form-container {
          width: 65%;
          min-height: 500px;
          border-radius: 20px;
          margin: 0 auto 140px;
          background: #3b3b3b;
          display: flex;
          flex-direction: column;
          align-items: center;
        }

        input,
        textarea {
          display: block;
        }

        .post-form-input {
          display: block;
          width: 80%;
          height: 40px;
          margin: 30px 0;
          text-indent: 15px;
          background: ${css.inputBackground};
          color: #ffffff;
          border: 2px solid ${css.inputBorder};
          font-size: 0.8rem;
        }

        .post-form-body {
          width: 80%;
          resize: none;
          height: 400px;
          margin-bottom: 40px;
          background: ${css.inputBackground};
          color: #ffffff;
          border: 2px solid ${css.inputBorder};
          padding: 15px;
        }

        .tags {
          width: 100%;
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          margin-top: -40px;
          margin-bottom: 40px;
          position: relative;
        }

        .tags-expand {
          display: none;
          width: 80%;
          height: 170px;
          background: ${css.inputBackground};
          border: 1px solid #bebebe;
          border-radius: 4px;
          position: absolute;
          bottom: 0;
          right: 0;
          transform: translate(-84px, 140px);
        }
      `}</style>
    </React.Fragment>
  );
};

export default RequestPostPage;
