import React, { useState } from "react";
import { useQuery } from "react-query";
import Link from "next/link";

import host from "../utils/host";
import { UserPostsType } from "../utils/types/userPostsType";
import * as css from "../utils/cssVariables";
import Tag from "./Tag";

interface Props {
  uuid: string;
}

const UserQuestions: React.FC<Props> = ({ uuid }) => {
  const [page, setPage] = useState<number>(1);

  const fetchUserPosts = async (page: number) => {
    const res = await fetch(
      `${host}/users/${uuid}/posts?page=${page}&limit=20`
    );
    return await res.json();
  };

  const { isLoading, isError, error, data, isPreviousData } = useQuery<
    UserPostsType,
    Error
  >(["user-posts", page], () => fetchUserPosts(page), {
    keepPreviousData: true,
    cacheTime: 0,
  });

  return (
    <React.Fragment>
      {isLoading ? (
        <div>Loading...</div>
      ) : isError ? (
        <div>{error.message}</div>
      ) : (
        <div className="user-posts-container">
          {data.posts.map((post) => (
            <div className="user-post-container" key={post.uuid}>
              <div className="post-aside">
                <div className="votes">
                  <p className="number">{post.votes}</p>
                  <p className="text">Vote{post.votes === 1 ? "" : "s"}</p>
                </div>
                <div className="comments">
                  <p className="number">{post.comments.length}</p>
                  <p className="text">
                    Answer{post.comments.length === 1 ? "" : "s"}
                  </p>
                </div>
              </div>
              <div className="post-main">
                <Link href={`/questions/${post.uuid}`}>
                  <p className="post-title">{post.title}</p>
                </Link>
                <div className="post-main-bottom">
                  <div className="communities-container">
                    {post.communities.slice(0, 4).map((community) => (
                      <Tag name={community.name} key={community.name} />
                    ))}
                  </div>
                </div>
              </div>
            </div>
          ))}
          <div className="page-handlers">
            <button
              onClick={() => setPage((old) => Math.max(old - 1, 0))}
              disabled={page === 1}
            >
              Previous Page
            </button>
            <button
              onClick={() => {
                if (!isPreviousData && data.hasMore) {
                  setPage((old) => old + 1);
                }
              }}
              disabled={isPreviousData || !data.hasMore}
            >
              Next Page
            </button>
          </div>
        </div>
      )}

      <style jsx>{`
        .user-post-container {
          height: 100px;
          background: ${css.floatingPostBackground};
          margin: 30px 20px;
          border-radius: 10px;
          padding: 15px;
          display: flex;
        }

        .post-aside {
          width: 3%;
          display: flex;
          flex-direction: column;
          justify-content: center;
          margin: 0 5px;
        }

        .votes,
        .comments {
          width: 100%;
          height: 50%;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        .votes p,
        .comments p {
          color: #dedede;
          font-size: 1rem;
        }

        .votes .text,
        .comments .text {
          font-size: 0.7rem;
        }

        .post-main {
          width: 97%;
          margin-left: 5px;
          border: 1px solid red;
        }
      `}</style>
    </React.Fragment>
  );
};

export default UserQuestions;
