import React from "react";

interface Props {
  uuid: string;
  name: string;
  description: string;
  count: number;
}

const Community: React.FC<Props> = ({ uuid, name, description, count }) => {
  return (
    <React.Fragment>
      <div className="community-container">
        <div className="name-count">
          <span className="background">
            <p>{name}</p>
          </span>
          <p>Number of Posts: {count}</p>
        </div>
        <p>{description}</p>
      </div>

      <style jsx>{`
        .community-container {
          width: 95%;
          height: 100px;
          margin: 0 auto;
          border-top: 1px solid #ffff;
        }

        .name-count {
          display: flex;
          justify-content: space-between;
          align-items: center;
          height: 30%;
        }

        .background {
          color: white;
          width: 30px;
          height: 20px;
        }
      `}</style>
    </React.Fragment>
  );
};

export default Community;
