import React, { ChangeEvent, useState, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";
import { useQuery } from "react-query";

import * as css from "../../utils/cssVariables";
import host from "../../utils/host";

const RequestPostPage: React.FC = () => {
  const [body, setBody] = useState<string>("**Hello World!**");
  const [tags, setTags] = useState<string>("");

  const fetchCommunities = async (page: number = 1, filter: string = "") => {
    const res = await fetch(
      `${host}/communities?page=${page}&limit=5&filter=${filter}`
    );
    const data: DataType = await res.json();
    return data;
  };

  useEffect(() => {
    if (tags !== "") {
    }
  }, [tags]);

  return (
    <React.Fragment>
      <h1 className="post-form-heading">Ask a Question</h1>
      <form className="post-form-container">
        <input
          type="text"
          className="post-form-input"
          id="title"
          placeholder="Title"
          required
        />
        <textarea
          id="body"
          className="post-form-body"
          cols={30}
          rows={10}
          placeholder="Body"
          required
          value={body}
          onChange={(e: ChangeEvent<HTMLTextAreaElement>) =>
            setBody(e.target.value)
          }
        />
        <ReactMarkdown
          className="preview"
          plugins={[remarkGfm]}
          source={body}
        />
        <div className="tags">
          <input
            type="text"
            className="post-form-input"
            placeholder="Tags: (maximum 5) "
            value={tags}
            onChange={(e: ChangeEvent<HTMLInputElement>) =>
              setTags(e.target.value)
            }
          />
        </div>
      </form>

      <style jsx>{`
        .post-form-heading {
          text-align: center;
          margin: 30px 0;
        }

        .post-form-container {
          width: 65%;
          min-height: 500px;
          border-radius: 20px;
          margin: 0 auto 50px;
          background: #3b3b3b;
          display: flex;
          flex-direction: column;
          align-items: center;
        }

        input,
        textarea {
          display: block;
        }

        .post-form-input {
          display: block;
          width: 80%;
          height: 40px;
          margin: 30px 0;
          text-indent: 15px;
          background: ${css.inputBackground};
          color: #ffffff;
          border: 2px solid ${css.inputBorder};
          font-size: 0.8rem;
        }

        .post-form-body {
          width: 80%;
          resize: none;
          height: 400px;
          margin-bottom: 40px;
          background: ${css.inputBackground};
          color: #ffffff;
          border: 2px solid ${css.inputBorder};
          padding: 15px;
        }

        .tags {
          width: 100%;
          display: flex;
          justify-content: center;
          margin-top: -40px;
        }
      `}</style>
    </React.Fragment>
  );
};

export default RequestPostPage;
