import React, { useState } from "react";
import { useQuery } from "react-query";

import host from "../utils/host";

const Communities: React.FC = () => {
  const [page, setPage] = useState<number>(1);

  const fetchCommunities = (page: number = 1) =>
    fetch(`${host}/communities?page=${page}&limit=20`);

  const {
    isLoading,
    isError,
    error,
    data,
    isFetching,
    isPreviousData,
  } = useQuery(["communities", page], () => fetchCommunities(page), {
    keepPreviousData: true,
  });

  return (
    <React.Fragment>
      <div className="communities-container">{isLoading}</div>
    </React.Fragment>
  );
};

export default Communities;
