import React from "react";

import * as css from "../utils/cssVariables";

interface Props {
  uuid: string;
  title: string;
  body: string;
  votes: number;
  commentLength: number;
  username: string;
}

const CommunityPost: React.FC<Props> = ({
  uuid,
  title,
  body,
  username,
  votes,
  commentLength,
}) => {
  return (
    <React.Fragment>
      <div className="community-post-container">
        <p>Votes: {votes}</p>
        <p>Answers: {commentLength}</p>
        <p>{title}</p>
        <p>{body}</p>
        <p>{username}</p>
      </div>

      <style jsx>{`
        .community-post-container {
          width: 90%;
          margin-top: 30px;
          background: ${css.floatingPostBackground};
          border-radius: 10px;
          height: 140px;
        }
      `}</style>
    </React.Fragment>
  );
};

export default CommunityPost;
