import React from "react";
import Link from "next/link";
import { useRouter } from "next/router";

import { tagBackground } from "../utils/cssVariables";

interface Props {
  name: string;
  isReload: boolean;
}

const Tag: React.FC<Props> = ({ name, isReload }) => {
  const router = useRouter();

  return (
    <React.Fragment>
      <div
        className="background"
        onClick={async () => {
          await router.push(`/${name}`);
          // if (isReload) {
          //   await router.reload();
          // }
        }}
      >
        <p className="name">{name}</p>
      </div>

      <style jsx>{`
        .background {
          background: ${tagBackground};
          border-radius: 4px;
          cursor: pointer;
          margin: 5px;
        }

        .background:hover {
          background: #3e3e3e;
        }

        .name {
          padding: 2px 7px;
        }

        .name:hover {
          color: #c6c6c6;
        }
      `}</style>
    </React.Fragment>
  );
};

export default Tag;
