import React, { useState } from "react";
import { useQuery } from "react-query";

import host from "../utils/host";
import Community from "./Community";

type CommunityType = {
  uuid: string;
  name: string;
  description: string;
  createdAt: string;
  updatedAt: string;
};

type DataType = {
  communities: CommunityType[];
  hasMore: boolean;
};

const Communities: React.FC = () => {
  const [page, setPage] = useState<number>(1);

  const fetchCommunities = (page: number = 1) => {
    fetch(`${host}/communities?page=${page}&limit=5`)
      .then((res) => res.json())
      .then((data) => {
        for (const community in data) {
          fetch(
            `${host}/communities/${community.uuid}/posts/count`
          ).then((res) => res.json());
        }
      });
  };

  const {
    isLoading,
    isError,
    error,
    data,
    isFetching,
    isPreviousData,
  } = useQuery<DataType, Error>(
    ["communities", page],
    () => fetchCommunities(page),
    {
      keepPreviousData: true,
    }
  );

  return (
    <React.Fragment>
      <div className="communities-container">
        {isLoading ? (
          <div>Loading...</div>
        ) : isError ? (
          <div>Error: {error.message}</div>
        ) : (
          <React.Fragment>
            {data.communities.map((community) => (
              <Community
                uuid={community.uuid}
                name={community.name}
                description={community.description}
                key={community.uuid}
              />
            ))}
            <span>Current Page: {page}</span>
            <button
              onClick={() => setPage((old) => Math.max(old - 1, 0))}
              disabled={page === 1}
            >
              Previous Page
            </button>
            <button
              onClick={() => {
                if (!isPreviousData && data.hasMore) {
                  setPage((old) => old + 1);
                }
              }}
              disabled={isPreviousData || !data.hasMore}
            >
              Next Page
            </button>
          </React.Fragment>
        )}
        {isFetching ? <span>Loading...</span> : null}
      </div>
    </React.Fragment>
  );
};

export default Communities;
