import React from "react";
import Link from "next/link";

import { tagBackground } from "../utils/cssVariables";

interface Props {
  name: string;
  uuid: string;
}

const Tag: React.FC<Props> = ({ name, uuid }) => {
  return (
    <React.Fragment>
      <Link href={`${name}`} prefetch={false}>
        <div className="background">
          <p className="name">{name}</p>
        </div>
      </Link>

      <style jsx>{`
        .background {
          background: ${tagBackground};
          border-radius: 4px;
          cursor: pointer;
          margin: 5px;
        }

        .background:hover {
          background: #3e3e3e;
        }

        .name {
          padding: 2px 7px;
        }

        .name:hover {
          color: #c6c6c6;
        }
      `}</style>
    </React.Fragment>
  );
};

export default Tag;
