import React from "react";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit, faTrashAlt } from "@fortawesome/free-solid-svg-icons";

interface Props {
  uuid: string;
  type: "question" | "answer";
}

const OPActions: React.FC<Props> = ({ uuid, type }) => {
  const style = {
    cursor: "pointer",
  };

  return (
    <React.Fragment>
      <div className="actions-container">
        <div className="icons-container">
          <FontAwesomeIcon
            icon={faEdit}
            color={"white"}
            style={style}
            size={"1x"}
          />
          <FontAwesomeIcon
            icon={faTrashAlt}
            color={"white"}
            style={style}
            size={"1x"}
          />
        </div>
      </div>

      <style jsx>{`
        .actions-container {
          width: 95%;
          clear: right;
          border: 1px solid red;
        }

        .icons-container {
          float: right;
        }
      `}</style>
    </React.Fragment>
  );
};

export default OPActions;
