import React, { useRef, useState } from "react";
import { useRouter } from "next/router";

import * as css from "../../../../utils/cssVariables";
import host from "../../../../utils/host";

const ResetPasswordPage: React.FC = () => {
  const router = useRouter();
  const { uuid } = router.query;

  const passwordRef = useRef<HTMLInputElement>(null);
  const confirmPasswordRef = useRef<HTMLInputElement>(null);
  const spinnerRef = useRef<HTMLDivElement>(null);
  const errMsgRef = useRef<HTMLParagraphElement>(null);

  const changePassword = (e) => {
    e.preventDefault();

    if (passwordRef.current.value === confirmPasswordRef.current.value) {
      fetch(`${host}/auth/email/password/reset`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          newPassword: passwordRef.current.value,
        }),
      })
        .then((res) => res.json())
        .then((data) => {
          if (data.status < 200 && data.status >= 300) {
            errMsgRef.current.innerText = data.msg;
          } else {
          }
        })
        .catch((err) => console.log(err));
    } else {
      errMsgRef.current.innerText = "Passwords don't match";
    }
  };

  return (
    <React.Fragment>
      <main>
        <form className="input-container" onSubmit={changePassword}>
          <input
            type="password"
            ref={passwordRef}
            placeholder="Enter new password:"
          />
          <input
            type="password"
            ref={confirmPasswordRef}
            placeholder="Confirm password:"
          />
          <button type="submit">Change password</button>
          <p className="err-msg" ref={errMsgRef} />
        </form>
      </main>

      <style jsx>{`
        main {
          width: 100%;
          height: 95vh;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .input-container {
          width: 30%;
          height: 55%;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        input {
          border: 1px solid ${css.inputBorder};
          background: ${css.inputBackground};
          color: #fff;
          height: 40px;
          width: 100%;
          text-indent: 15px;
          font-size: 0.8rem;
          margin: 10px 0;
        }

        button {
          border: none;
          background: ${css.mainButton};
          color: white;
          width: 150px;
          height: 30px;
          border-radius: 4px;
          cursor: pointer;
          margin: 20px 0;
        }

        button:hover {
          background: ${css.mainButtonHover};
        }

        .err-msg {
          color: #ff0000;
          text-align: center;
          width: 90%;
        }
      `}</style>
    </React.Fragment>
  );
};

export default ResetPasswordPage;
