import React, { useContext, useRef, useState } from "react";
import { useQuery } from "react-query";
import { GetServerSideProps } from "next";

import host from "../../../utils/host";
import { UserContext } from "../../../context/UserContext";

type UserType = {
  uuid: string;
  username: string;
  email: string;
  verified: boolean;
  reputation: number;
  role: string;
  createdAt: string;
  updatedAt: string;
};

interface Props {
  username: string;
}

const UserProfilePage: React.FC<Props> = ({ username }) => {
  const [tab, setTab] = useState<"question" | "answer">("question");

  const userContext = useContext(UserContext);

  const fetchUser = async () => {
    const res = await fetch(`${host}/users/${username}`);
    return await res.json();
  };

  const { isLoading, isError, error, data } = useQuery<UserType, Error>(
    "user",
    () => fetchUser(),
    { cacheTime: 0 }
  );

  function handleTab(tab: "question" | "answer") {
    const tabs = document.getElementsByClassName("nav-item");

    for (let i = 0; i < tabs.length; i++) {
      const tab = tabs[i] as HTMLElement;
      tab.classList.remove("active");
    }

    const activeTab = tab === "question" ? "answer" : "question";
    const activeTabElement = document.getElementById(
      activeTab
    ) as HTMLSpanElement;
    console.log(activeTabElement);

    activeTabElement.classList.add("active");
    setTab(activeTab);
  }

  return (
    <React.Fragment>
      {isLoading ? (
        <div>Loading...</div>
      ) : isError ? (
        <div>Error: {error.message}</div>
      ) : (
        <div className="user-container">
          <h1>{data.username}</h1>
          <p>Reputation: {data.reputation}</p>
          {data.uuid === userContext?.user?.uuid ? (
            <p>Email: {data.email}</p>
          ) : (
            <div />
          )}
          <div className="tab-nav">
            <span
              className="nav-item active"
              id="question"
              onClick={() => handleTab("question")}
            >
              <p>Questions</p>
            </span>
            <span
              className="nav-item"
              id="answer"
              onClick={() => handleTab("answer")}
            >
              <p>Answers</p>
            </span>
          </div>
          <div className="user-content">
            {tab === "question" ? <div>Question</div> : <div>Answer</div>}
          </div>
        </div>
      )}

      <style jsx>{`
        .user-container {
          margin: 40px;
        }

        .tab-nav {
          margin-top: 30px;
          width: 100%;
          height: 40px;
          display: flex;
          justify-content: flex-start;
          background: #414141;
          border: 1.5px solid #cdcdcd;
        }

        .nav-item {
          height: 100%;
          width: 90px;
          display: flex;
          justify-content: center;
          align-items: center;
          cursor: pointer;
        }

        .nav-item:hover {
          background: #7c7c7c;
        }

        .active {
          background: #565656;
        }

        .active:hover {
          background: #a0a0a0;
        }

        .user-content {
          border-left: 1.5px solid #cdcdcd;
          border-right: 1.5px solid #cdcdcd;
          border-bottom: 1.5px solid #cdcdcd;
        }
      `}</style>
    </React.Fragment>
  );
};

export const getServerSideProps: GetServerSideProps = async (context) => {
  return {
    props: {
      username: context.query.username,
    },
  };
};

export default UserProfilePage;
