import React from "react";

interface Props {
  formType: "signup" | "login";
}

const AuthForm: React.FC<Props> = ({ formType }) => {
  const submitForm = (e) => {
    e.preventDefault();
  };

  return (
    <React.Fragment>
      <div className="auth-form-page-container">
        <form className="auth-form-container" onSubmit={submitForm}>
          <h1 className="auth-form-heading">
            {formType === "signup" ? "Signup" : "Login"}
          </h1>
          {formType === "signup" ? (
            <div className="auth-form auth-form-signup">
              <input
                type="text"
                className="auth-form-input"
                id="username"
                placeholder="Username: "
                required
              />
              <input
                type="text"
                className="auth-form-input"
                id="email"
                placeholder="Email: "
                required
              />
              <input
                type="text"
                className="auth-form-input"
                id="password"
                placeholder="Password: "
                required
              />
              <input
                type="text"
                className="auth-form-input"
                id="confirmPassword"
                placeholder="Confirm Password: "
                required
              />
              <div className="auth-form-checkbox-container">
                <input
                  type="checkbox"
                  className="auth-form-checkbox"
                  id="rememberMe"
                />
                <label
                  htmlFor="rememberMe"
                  className="auth-form-checkbox-label"
                >
                  Remember Me
                </label>
              </div>
              <button type="submit" className="auth-form-submit-btn">
                Signup
              </button>
            </div>
          ) : (
            <h1>Login</h1>
          )}
        </form>
      </div>

      <style jsx>{`
        .auth-form-page-container {
          width: 100%;
          height: calc(100vh - 40px - 4px);
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .auth-form-container {
          height: 80%;
          width: 30%;
          min-width: 310px;
          transform: translateY(-30px);
          border: 1px solid red;
        }

        .auth-form-heading {
          text-align: center;
          transform: translateY(70px);
        }

        .auth-form {
          display: flex;
          flex-direction: column;
          justify-content: flex-start;
          align-items: center;
          width: 100%;
          height: 100%;
        }

        .auth-form-input {
          display: block;
          width: 100%;
          height: 40px;
          margin: 11px 0;
          text-indent: 5px;
          background: #454545;
          color: white;
          border: 2px solid #dedede;
        }

        .auth-form-input::placeholder {
          font-size: 0.8rem;
          color: white;
        }

        #username {
          margin-top: 90px;
        }

        .auth-form-checkbox-container {
          margin: 10px 0;
          border: 1px solid red;
          width: 100%;
          display: flex;
          justify-content: space-around;
        }
      `}</style>
    </React.Fragment>
  );
};

export default AuthForm;
