import React from "react";

interface Props {
  uuid: string;
  name: string;
  description: string;
  count: number;
}

const Community: React.FC<Props> = ({ uuid, name, description, count }) => {
  return (
    <React.Fragment>
      <div className="community-container">
        <div className="name-count">
          <p>{name}</p>
          <p>Number of Posts: {count}</p>
        </div>
        <p>{description}</p>
      </div>

      <style jsx>{`
        .community-container {
          width: 95%;
          height: 100px;
          margin: 0 auto;
          border-top: 1px solid #ffff;
        }

        .name-count {
          display: flex;
          justify-content: space-between;
          border: 1px solid red;
        }
      `}</style>
    </React.Fragment>
  );
};

export default Community;
