import React from "react";

import { QuestionType } from "../utils/types/individualQuestionType";
import * as css from "../utils/cssVariables";

interface Props {
  setEditMode: React.Dispatch<React.SetStateAction<boolean>>;
  question: QuestionType;
}

const EditPost: React.FC<Props> = ({ setEditMode, question }) => {
  return (
    <React.Fragment>
      <form className="edit-post-container">
        <textarea id="edit-form-body" cols={30} rows={10} />
      </form>

      <style jsx>{`
        .edit-post-container {
          width: 92%;
          margin: 20px auto;
          height: 200px;
          border: 1px solid red;
        }

        .edit-form-body {
          width: 90%;
          resize: none;
          height: 400px;
          margin-bottom: 20px;
        }
      `}</style>
    </React.Fragment>
  );
};

export default EditPost;
