import React, { useState, useRef } from "react";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";

import * as css from "../utils/cssVariables";
import { CommentType } from "../utils/types/individualQuestionType";

interface Props {
  setEditMode: React.Dispatch<React.SetStateAction<boolean>>;
  comment: CommentType;
}

const EditAnswer: React.FC<Props> = ({ setEditMode, comment }) => {
  const [body, setBody] = useState<string>(comment.body);

  const spinner = useRef(null);

  return (
    <React.Fragment>
      <form className="edit-answer-body">
        <textarea
          id="edit-form-body"
          cols={30}
          rows={10}
          value={body}
          onChange={(e) => setBody(e.target.value)}
        />
        <ReactMarkdown
          className="preview edit-answer-preview"
          source={body}
          plugins={[remarkGfm]}
        />
      </form>

      <style jsx>{`
        .edit-answer-body {
          margin: 20px auto;
          width: 90%;
        }

        #edit-form-body {
          width: 100%;
          resize: none;
          height: 200px;
          margin-bottom: 20px;
          background: ${css.inputBackground};
          border: 2px solid ${css.inputBorder};
          color: #fff;
          padding: 15px;
        }
      `}</style>
    </React.Fragment>
  );
};

export default EditAnswer;
