import React, { useEffect, useState } from "react";
import { useRouter } from "next/router";
import { useQuery } from "react-query";

import host from "../../utils/host";
import { CommunityPostsType } from "../../utils/types/postType";

const CommunityPostPage: React.FC = () => {
  const [uuid, setUuid] = useState<string>("");
  const [page, setPage] = useState<number>(1);
  const router = useRouter();
  const { community } = router.query;

  const fetchPosts = async (page: number) => {
    if (uuid) {
      const res = await fetch(
        `${host}/communities/${uuid}/posts?page=${page}&limit=10`
      );
      return await res.json();
    }
  };

  const {
    isLoading,
    isError,
    error,
    data,
    isFetching,
    isPreviousData,
  } = useQuery<CommunityPostsType, Error>(
    ["community-posts", page],
    () => fetchPosts(page),
    {
      keepPreviousData: true,
    }
  );

  useEffect(() => {
    setUuid(localStorage.getItem("communityUuid"));
  }, []);

  return (
    <React.Fragment>
      <div>
        {isLoading ? (
          <div>Loading...</div>
        ) : isError ? (
          <div>{error.message}</div>
        ) : (
          data.posts.map((post) => (
            <div key={post.uuid}>
              <p>{post.title}</p>
              <p>{post.body}</p>
            </div>
          ))
        )}
      </div>
    </React.Fragment>
  );
};

export default CommunityPostPage;
