import React, { useContext } from "react";
import { useRouter } from "next/router";
import Link from "next/link";

import { UserContext } from "../context/UserContext";
import host from "../utils/host";
import * as css from "../utils/cssVariables";

interface Props {
  formType: "signup" | "login";
}

const AuthForm: React.FC<Props> = ({ formType }) => {
  const userContext = useContext(UserContext);
  const router = useRouter();

  const submitForm = (e) => {
    e.preventDefault();

    const username = document?.getElementById("username") as HTMLInputElement;
    const email = document?.getElementById("email") as HTMLInputElement;
    const password = document?.getElementById("password") as HTMLInputElement;
    const confirmPassword = document?.getElementById(
      "confirmPassword"
    ) as HTMLInputElement;
    const rememberMe = document?.getElementById(
      "rememberMe"
    ) as HTMLInputElement;
    const identifier = document?.getElementById(
      "identifier"
    ) as HTMLInputElement;
    const errorMsg = document?.getElementById("error-msg") as HTMLInputElement;

    if (formType === "signup") {
      if (password.value === confirmPassword.value) {
        fetch(`${host}/auth/signup`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          credentials: "include",
          body: JSON.stringify({
            username: username.value,
            email: email.value,
            password: password.value,
            rememberMe: rememberMe.checked,
          }),
        })
          .then((res) => res.json())
          .then(async (data) => {
            if (data.errors) {
              errorMsg.innerText = data.errors[0].msg;
            } else if (data.msg) {
              errorMsg.innerText = data.msg;
            } else {
              errorMsg.innerText = "";
              const payload = JSON.parse(atob(data.token.split(".")[1]));
              fetch(`${host}/users/${payload.uuid}`)
                .then((res) => res.json())
                .then((data) => userContext.setState(data))
                .catch((err) => console.log(err));
              await router.push("/");
            }
          })
          .catch((err) => console.log(err));
      } else {
        errorMsg.innerText = "Password doesn't match";
        console.log("ddd");
      }
    } else if (formType === "login") {
      if (identifier.value.includes("@")) {
        fetch(`${host}/auth/login`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          credentials: "include",
          body: JSON.stringify({
            email: identifier.value,
            password: password.value,
            rememberMe: rememberMe.checked,
          }),
        })
          .then((res) => res.json())
          .then(async (data) => {
            if (data.msg) {
              errorMsg.innerText = data.msg;
            } else {
              errorMsg.innerText = "";
              const payload = JSON.parse(atob(data.token.split(".")[1]));
              fetch(`${host}/users/${payload.uuid}`)
                .then((res) => res.json())
                .then((data) => userContext.setState(data))
                .catch((err) => console.log(err));
              await router.push("/");
            }
          })
          .catch((err) => console.log(err));
      } else {
        fetch(`${host}/auth/login`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          credentials: "include",
          body: JSON.stringify({
            username: identifier.value,
            password: password.value,
            rememberMe: rememberMe.checked,
          }),
        })
          .then((res) => res.json())
          .then(async (data) => {
            if (data.msg) {
              errorMsg.innerText = data.msg;
            } else {
              errorMsg.innerText = "";
              const payload = JSON.parse(atob(data.token.split(".")[1]));
              fetch(`${host}/users/${payload.uuid}`)
                .then((res) => res.json())
                .then((data) => userContext.setState(data))
                .catch((err) => console.log(err));
              await router.push("/");
            }
          })
          .catch((err) => console.log(err));
      }
    }
  };

  return (
    <React.Fragment>
      <div className="auth-form-page-container">
        <form className="auth-form-container" onSubmit={submitForm}>
          <h1 className="auth-form-heading">
            {formType === "signup" ? "Signup" : "Login"}
          </h1>
          {formType === "signup" ? (
            <div className="auth-form auth-form-signup">
              <input
                type="text"
                className="auth-form-input"
                id="username"
                placeholder="Username: "
                required
              />
              <input
                type="text"
                className="auth-form-input"
                id="email"
                placeholder="Email: "
                required
              />
              <input
                type="password"
                className="auth-form-input"
                id="password"
                placeholder="Password: "
                required
              />
              <input
                type="password"
                className="auth-form-input"
                id="confirmPassword"
                placeholder="Confirm Password: "
                required
              />
              <div className="auth-form-checkbox-container">
                <div className="checkbox-remember-me">
                  <input
                    type="checkbox"
                    className="auth-form-checkbox"
                    id="rememberMe"
                  />
                  <label
                    htmlFor="rememberMe"
                    className="auth-form-checkbox-label"
                  >
                    Remember Me
                  </label>
                </div>
                <p className="auth-form-forgot-password">
                  Already have an account?
                </p>
              </div>
              <div className="auth-form-error-msg" id="error-msg" />
              <button type="submit" className="auth-form-submit-btn">
                Signup
              </button>
            </div>
          ) : (
            <div className="auth-form auth-form-login">
              <input
                type="text"
                className="auth-form-input"
                id="identifier"
                placeholder="Username or Email: "
                required
              />
              <input
                type="password"
                className="auth-form-input"
                id="password"
                placeholder="Password: "
                required
              />
