import React from "react";

import * as css from "../utils/cssVariables";
import { PostType } from "../utils/types/postType";
import Tag from "./Tag";

interface Props {
  post: PostType;
}

const CommunityPost: React.FC<Props> = ({ post }) => {
  return (
    <React.Fragment>
      <div className="community-post-container">
        <div className="community-post-aside">
          <div className="votes-container">
            <p>{post.votes}</p>
            <p>Vote{post.votes === 1 ? "" : "s"}</p>
          </div>
          <div className="comments-container">
            <p>{post.comments.length}</p>
            <p>Answer{post.comments.length === 1 ? "" : "s"}</p>
          </div>
        </div>
        <div className="community-post-main">
          <p>{post.title} </p>
          <div className="post-communities-container">
            {post.communities.map((community) => (
              <Tag name={community.name} key={community.name} />
            ))}
            <p>asked by: {post.user.username}</p>
          </div>
        </div>
      </div>

      <style jsx>{`
        .community-post-container {
          width: 90%;
          margin-top: 30px;
          background: ${css.floatingPostBackground};
          border-radius: 10px;
          height: 100px;
          display: flex;
        }

        .community-post-aside {
          width: 10%;
          display: flex;
          flex-direction: column;
          justify-content: center;
        }

        .community-post-main {
          width: 90%;
        }

        .post-communities-container {
          display: flex;
        }

        .votes-container,
        .comments-container {
          border: 1px solid red;
          width: 100%;
          height: 50%;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        .votes-container p,
        .comments-container p {
          color: #dedede;
        }
      `}</style>
    </React.Fragment>
  );
};

export default CommunityPost;
