export function getCookie(cname) {
  if (true) {
    var name = cname + "=";
    var decodedCookie = decodeURIComponent(document.cookie);
    var ca = decodedCookie.split(";");

    for (var i = 0; i < ca.length; i++) {
      var c = ca[i];

      while (c.charAt(0) == " ") {
        c = c.substring(1);
      }

      if (c.indexOf(name) == 0) {
        return c.substring(name.length, c.length);
      }
    }

    return "";
  }
}
export function getTime(time) {
  var now = Date.now();
  var comparedTime = Date.parse(time);
  var difference = Math.abs(comparedTime - now);

  if (difference <= 60000) {
    return "".concat(Math.round(difference / 1000).toString(), " secs ago");
  } else if (difference < 3600000) {
    return "".concat(Math.round(difference / 60000).toString(), " mins ago");
  } else if (difference < 86400000) {
    return "".concat(Math.round(difference / 3600000).toString(), " hrs ago");
  } else if (difference < 2678400000) {
    return "".concat(Math.round(difference / 86400000).toString(), " days ago");
  } else if (difference < 31557600000) {
    return "".concat(Math.round(difference / 2629800000).toString(), " months ago");
  } else if (difference >= 31557600000) {
    return "".concat(Math.round(difference / 31557600000).toString(), " yrs ago");
  }
}
